function getCity(e){$.get(e,function(e){$("main").html(e)})}function loadCity(){var e=$(".select-city").val();if(""!==e){getCity("./assets/cities/city-"+e+".html")}}function filterDb(e){var t,n,l,o,i,a,s;for(t="input-"+e,t=t.toString(),console.log(t),n=document.getElementById(t),l=n.value.toUpperCase(),o=document.getElementById("data-table"),i=o.getElementsByTagName("tr"),s=0;s<i.length;s++)(a=i[s].getElementsByTagName("td")[e])&&(a.innerHTML.toUpperCase().indexOf(l)>-1?(i[s].style.display="",console.log("here")):(i[s].style.display="none",console.log("not here")))}