function getCity(e){$.get(e,function(e){$("main").html(e)})}function loadCity(){var e=$(".select-city").val();if(""!==e){getCity("./_includes/city-"+e+".html")}}function filterDb(e){var t,n,l,o,i,a,g;for(t="input-"+e,t=t.toString(),console.log(t),n=document.getElementById(t),l=n.value.toUpperCase(),o=document.getElementById("data-table"),i=o.getElementsByTagName("tr"),g=0;g<i.length;g++)(a=i[g].getElementsByTagName("td")[e])&&(a.innerHTML.toUpperCase().indexOf(l)>-1?(i[g].style.display="",console.log("here")):(i[g].style.display="none",console.log("not here")))}